import {useState} from 'react';
import './App.css';
import { BiSearch } from 'react-icons/bi';


function App() {
  const [state,setState]=useState({status:true,bgColor:"rgb(189, 32, 197)"});

  const searchStyle={
    color:"red",
    cursor:"pointer",
    fontSize:"2.5rem",
    position:"absolute",
    top:"2%",
    right:"2%"};


    function handleClick(){
      if (state){
        setState({status:false,bgColor:"black"});
        document.body.style.backgroundColor=state.bgColor;
      }
      else{
        setState(true);
        document.body.style.backgroundColor="rgb(189, 32, 197)";
      }
    }

  return (
    <div className="App" onClick={handleClick}>
      {(state) ? <BiSearch style={searchStyle} onClick={handleClick}/> : <input type="search" placeholder="Search" id="inputBox" />}
    </div>
  );
}

export default App;
