import {useState} from 'react';
import './App.css';
import { BiSearch } from 'react-icons/bi';


function App() {
  const [state,setState]=useState({status:true,bgColor:"rgb(189, 32, 197)"});

  const searchStyle={
    color:"white",
    cursor:"pointer",
    fontSize:"2.5rem",
    position:"absolute",
    top:"2%",
    right:"2%",
    background:state.bgColor};


    function handleClick(){
      if (state.status){
        setState({status:false,bgColor:"black"});
        document.body.style.backgroundColor=state.bgColor;
      }
      else{
        setState({status:true,bgColor:"rgb(189, 32, 197)"});
        document.body.style.backgroundColor=state.bgColor;
      }
    }

  return (
    <div className="App" onClick={handleClick}>
      {(state.status) ? <BiSearch style={searchStyle} onClick={handleClick}/> : <input type="search" placeholder="Search" id="inputBox" />}
    </div>
  );
}

export default App;
