import {useState} from 'react';
import './App.css';
import { BiSearch } from 'react-icons/bi';


function App() {
  const [state,setState]=useState(true);

  const searchStyle={
    color:"white",
    cursor:"pointer",
    fontSize:"2.5rem",
    position:"absolute",
    top:"2%",
    right:"2%"};


    function handleClick(){
      if (state){
        setState(false);
        document.querySelector("body").style.backgroundColor=""
      }
    }

  return (
    <div className="App">
      {(state) ? <BiSearch style={searchStyle} onClick={handleClick}/> : <input type="search" placeholder="Search" id="inputBox" />}
    </div>
  );
}

export default App;
