function PopUpButton({title,statement}){

    const buttonStyling={
    border:"1px solid black",
    padding:"4%",
    backgroundColor:"orange"
    }

    return(
        <>
    <div className="button-style" style={buttonStyling}>
     <p>{title}</p>
     <p>{statement}</p>
    </div>
     <p>X</p>
    </>)
}

export default PopUpButton;
