import './App.css';
import { useState,useEffect } from 'react';
import PopUpButton from './Components/PopUpButton';

function App() {

  const [state, setState] = useState(false);
  const [newState,setNewState]=useState(false)
  useEffect(() => {
    setTimeout(() => {
      setState(true);
    }, 3000)
  },[])

  return (
    <div className="App">
      <h1>Magic Popup App</h1>
      <button onClick={()=>{setState(false)}}>Click me</button>
      {state && <PopUpButton title="Time out Popup" statement="This popup was trigerred by a delay" />}
      {!state && <PopUpButton title="Trigerred Popup" statement="This popup was trigerred by you" />}

    </div>
  );
}

export default App;
