import './App.css';
import { useState,useEffect } from 'react';
import PopUpButton from './Components/PopUpButton';

function App() {

  const [state, setState] = useState(false);


  const [newState,setNewState]=useState(false);

  useEffect(() => {

    setTimeout(() => {
      setState(true);
    }, 3000)

  },[])

  function handleClick(){
    if(state.flag){
      alert("Close TimeOut PopUp")
      return;
    }
    else{
    setNewState(true);
  }
}
  return (
    <div className="App">
      <h1>Magic Popup App</h1>
      <button onClick={handleClick}>Click me</button>
      {state && <PopUpButton style={{backgroundColor:"rgba(255, 165, 0,0.35)"}} title="Time out PopUp" statement="This popup was trigerred by a delay" />}
      {newState && <PopUpButton style={{backgroundColor:"rgba(61, 61, 247,0.25)"}} title="Trigerred Popup" statement="This popup was trigerred by you" />}

    </div>
  );
}

export default App;
