    <div className="button-style" style={{backgroundColor:bgColor}}>
    <div>
     <p>{title}</p>
     <p>{statement}</p>
    </div>
     <p onClick={(e)=>{
    e.target.parentElement.style.display="none";
    }} style={{cursor:"pointer"}}>X</p>
    </div>)
}

