function Tracker({state,bgColor,handleChange}){

    const thumbStyling={
        width:`${state}%`,
        backgroundColor:`${bgColor}`,
        color:"white",
        borderRadius:"2vw",
        paddingLeft:"1%"
    }
    return(
    <>
    <div className="tracker">
        <div style={thumbStyling} className="thumb" onChange={handleChange}>{state}%</div>
    </div>

    </>)
}

export default Tracker;
