import {useState,useEffect,useRef} from 'react';
import './App.css';
import Tracker from './Components/Tracker';


const data=[{
  initialState:"55" ,
  bgColor:"#6464e2"
},{
  initialState:"33" ,
  bgColor:"#16b516"
},{
  initialState:"53" ,
  bgColor:"red"
}];

function App() {

const [trackerState,setTrackerState]=useState(0);

const inputRef=useRef();

const [flagState,setFlagSate]=useState(true);

useEffect(()=>{
inputRef.current.focus();
setFlagSate(false);
},[])


function handleChange(e){
if(e.target.value < 0 || e.target.value > 100){
alert("Enter a number between 0 and 100");
e.target.value="";
setTrackerState(0);
}
else{
  setTrackerState(e.target.value);
}
}


  return (
    <div className="App">

      <h1>Progress Bar</h1>
      <h2>Project status:</h2>

      <div className='container-1'>
        <span>  UI status: <input type="number" ref={inputRef} onChange={handleChange}/> </span>
        <span> UX status: <input type="number" onChange={handleChange}/></span>
        <span> DATA status: <input type="number" onChange={handleChange}/></span>
      </div>
      <div className='container-2'>
      {data.map((val,idx)=>{
        {/* if(flagState)
        setTrackerState(val.initialState); */}

        return (<Tracker key={idx} state={(flagState) ? val.initialState :trackerState} bgColor={val.bgColor} onChange={handleChange} />)
      })}
        {/* <Tracker/> */}
      </div>
    </div>
  );
}

export default App;
