import {useState,useEffect,useRef} from 'react';
import './App.css';

function App() {

  const inputRef=useRef();

  return (
    <div className="App">

      <h1>Progress Bar</h1>
      <h2>Project status:</h2>

      <div className='container-1'>
        <span>  UI status: <input type="number" ref={inputRef} /> </span>
        <span> UX status: <input type="number" /></span>
        <span> DATA status: <input type="number" /></span>
      </div>
      <div className='container-2'></div>
    </div>
  );
}

export default App;
