import Button from './Button';
import { useState } from 'react';

function DialogueBox() {


    const [state,setState]=useState({
    bgColor:"grey",
    title:"Hello",
    description:"Would you like to Subscribe?"
  });

  document.body.style.backgroundColor=state.bgColor;

function handleClick(e){

if (e.target.innerText==="Cancel"){
setState({
    bgColor:"grey",
    title:"Hello",
    description:"Would you like to Subscribe?"
})
}
else if(e.target.innerText==="Subscribe"){
    setState({bgColor:"grey",
    title:"Hello",
    description:"Would you like to Subscribe?"})
}
else{
    setState({bgColor:"grey",
    title:"Hello",
    description:"Would you like to Subscribe?"})
}
}

    return (
        <>
            <div className="box-1">
                <div className="text">
                    <h1>{state.title}</h1>
                    <h2>{state.description}</h2>
                </div>
                <div>Icon</div>
            </div>
            <div className="box-2">

            {(state.title==="Hello") && <><Button text="Cancel" onClick={handleClick}/>
            <Button text="Subscribe" onClick={handleClick}/></>}

            {(state.title==="Thank you") && <Button text="Unsubscribe" onClick={handleClick}/>}

            {(state.title==="Sorry") && <Button text="Subscribe" onClick={handleClick}/>}

                {/* <Button text="Cancel" onClick={handleClick}/>
                
                <Button text="UnSubscribe" onClick={handleClick}/> */}
            </div>

        </>)
}

export default DialogueBox;
