import Card from './Components/Card';
import './App.css';
import {useState,useEffect} from 'react'; 




function App() {

  const [userData,setUserData]=useState();
  const [currentData,setCurrentData]=useState();

useEffect(()=>{
  fetch('https://dummyjson.com/users')
.then(res => res.json())
.then(json =>{ 
        setUserData(json)
        setCurrentData(json) })
},[])


function handleChange(e){
  if(currentData.name==="")
  setCurrentData(userData);
  else{
  setCurrentData(userData.filter((val)=>val.name.toLowerCase().includes(e.target.value.toLowerCase())))
}
}

  return (
    <div className="App">
    <h1 style={{fontSize:"2.5rem"}} >Search Filter App</h1>
    <input type="text" placeholder='Search by first name' onChange={handleChange}/>
    <div className="card-box">
      {currentData?.map((val)=>{
        return(<Card data={val}/>)
      })}
      </div>
    </div>
  ) 
}

export default App;
