function Comment({data}){
    
    return(
    <>
        {
            data?.map((val)=>{

            return ( <div className="posts-style">
                <p style={{"fontWeight":"bold"}}>{val.name}</p>
                <p>{val.body}</p>
                <p>{val.email}</p>
                </div>
            )
            }
        )}
    </>);
}

export default Comment;
