import './App.css';
import Box from "./Components/Box";

const data=[
  {
  text:"Complete all fields",
  color:"red" 
  },
  {
  text:"Successful Login",
  color:"green"
  },
  {
  text:"Hide alert after delay",
  color:"blue"}
]

function App() {

  return (
    <>
    {
      data.map(()=>{<Box text={"Complete all fields"} colour={data.color}/>})
    }
    </>
  );
}

export default App;
