import { createContext } from "react";

const BlogContext=createContext();

const BlogProvider=({children})=>{


    return(
        <BlogContext.Provider value="Hello">
            {children}
        </BlogContext.Provider>
    )
}

export default BlogContext;
export {BlogProvider};
