import { useState } from 'react';
import { AiFillUnlock } from 'react-icons/ai';
import HomePic from '../Images/home.jpg'
import MoonPic from '../Images/moon.jpg'



function SlideToUnlock() {

    const [state, setState] = useState({
        title: "Unlock Screen",
        color: "white",
        bgImg: `url(${MoonPic})`
    });

    const [switchState, setSwitchState] = useState(true);

    function handleValue(e) {
        console.log(e.target.value);
        if (e.target.value >= 99) {
            setState({ title: "Lock Screen", color: "black", bgImg: `url(${HomePic})` })
            setSwitchState(false);
        }
        // else {
        //     setState({
        //         title: "Unlock Screen",
        //         color: "white",
        //         bgImg: `url(${MoonPic}) center/cover no repeat`
        //     })
        //     setSwitchState(true);
        }


    // const styling={ background: state.bgImg, color: state.color ,backgroundSize: "cover" };

    return (
        <div className="locked-screen" style={{color:"blue",border:"2px solid red"}}>
            <h1>{state.title}</h1>
            <input className="test"  type="range" onInput={handleValue} />
            {/* {(switchState) ? <input className="test" type="range" onInput={handleValue} /> : <AiFillUnlock className="unlock-button" />} */}
        </div>)
}

export default SlideToUnlock;
