import {useState} from 'react';
import {AiFillUnlock} from 'react-icons/ai';
import HomePic from '../Images/home.jpg'
import MoonPic from '../Images/moon.jpg'



function SlideToUnlock(){

 const[state,setState]=useState({
    title:"Unlock Screen",
    color:"white",
    bgImg:`url(${MoonPic})`});

    const [switchState,setSwitchState]=useState(true);

  function handleValue(e){
    console.log(e.target.value);
    if(e.target.value == 100){
    setState({title:"Lock Screen",bgImg:`url(${HomePic})`,color:"black"})
    setSwitchState(false);
    }
    else
    setSwitchState(true);

  }

    return(
        (<div className="locked-screen" style={{background:`${state.bgImg} no-repeat`,color:`${state.color}`}}>
         <h1>{state.title}</h1>
      {(switchState) ? <input className="test" style={{background:`${state.url} no-repeat`}} type="range"  onInput={handleValue} /> : <AiFillUnlock style={{color:`white`}}/>}
     </div>) )
}

export default SlideToUnlock;
