import {useState} from 'react';
import './App.css';

function App() {
  const [state,setState]=useState(0);

  function handleInput(e){
    setState(e.target.value);
  }

  const styling={width:`${Math.ceil(state*3.5)}px`,height:`${Math.ceil(state*3.5)}px`,backgroundColor:state.color}

  return (
    <div className="App">
      <h1>Slide to Grow</h1>
      <input type="range" onInput={handleInput} />
      <p className="paraBox" style={styling}>{state}</p>
    </div>
  );
}

export default App;
